package tasks;

import algoribrary.io.InputReader;

import java.io.PrintWriter;
import java.math.BigInteger;

public class TaskE {
    public void solve(final int testNumber, final InputReader in, final PrintWriter out) {
        final String number = in.next();
        {
            StringBuilder sb = new StringBuilder("1");
            for (int i = 0; i < 100_000; ++i) sb.append('0');
            if (sb.toString().equals(number)) {
                out.println("");
                return;
            }
        }
        final BigInteger num = new BigInteger(number.charAt(0) == '-' ? number.substring(1) : number);
        if (num.equals(BigInteger.ONE)) {
            out.println(1);
            return;
        }
        if (num.equals(BigInteger.ZERO)) {
            out.println(3);
            return;
        }

        final int BUBEN = 15;
        final BigInteger mul = num.shiftLeft(1);
        BigInteger cur = BigInteger.ONE.shiftLeft(mul.bitLength() / 2);
        for (int i = 0; i < BUBEN; ++i) {
            cur = cur.add(BigInteger.ONE).add(mul.divide(cur)).shiftRight(1);
        }
        cur = cur.subtract(BigInteger.ONE);
//        System.err.println(cur);

        while (true) {
            BigInteger next = cur.add(BigInteger.ONE);
            BigInteger max = cur.multiply(next); //.shiftRight(1);
            if (mul.testBit(0) == max.testBit(0) && mul.testBit(1) == max.testBit(1) && max.compareTo(mul) >= 0) break;
            cur = next;
        }
        out.println(cur);
    }
}
